    fun translate(s: String): String {
        var m: MutableMap<String, String> = mutableMapOf("١" to "1", "٢" to "2", "٣" to "3", "٤" to "4", "٥" to "5", "٦" to "6", "٧" to "7", "٨" to "8", "٩" to "9", "٠" to "0")
        var res: String = ""
        for (c in s) {
            var temp: String = c.toString()
            if (m.containsKey(temp)) {
                res += m[temp]
            } else res += temp
        }
        return res
    }


